import React, { Component } from 'react';
import logo from './logo.svg';
import './App.css';

class App extends Component {

  constructor(props) {
    super(props);

    var todayhour = new Date(),
        hour = todayhour.getHours() + ':' + todayhour.getMinutes() + '!';

    var todaydate = new Date(),
        date = todaydate.getDate() + '-' + (todaydate.getMonth() + 1) + '-' + todaydate.getFullYear();

    var todaymonth = new Date(),
        month = todaymonth.getMonth();

    var todayyear = new Date(),
        year = todayyear.getFullYear();

    var MthPercentage = (month / 12) * 100;

    this.state = {
        date: date,
        hour: hour,
        year: year,
        month: month,
        MthPercentage: MthPercentage
    };

}

  render() {
    var loadingbar = <div className="bar_container"><div className="bar"></div></div>;
    return (
      <div className="progress_bar">
        <label>{this.state.month}</label>
        {loadingbar}
      </div>
    );
  }
}

export default App;
